import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { ChartContainer, ChartTooltip, ChartTooltipContent } from "@/components/ui/chart";
import { Bar, BarChart, CartesianGrid, XAxis, YAxis, Legend } from "recharts";

interface TddBandsChartProps {
  tddCode: string;
  title?: string;
  description?: string;
}

// TDD pásma (simplified visualization)
const TDD_BANDS = {
  C01d: { VT: 0.2, NT: 0.5, VolT: 0.3 },
  C02d: { VT: 0.25, NT: 0.5, VolT: 0.25 },
  C03d: { VT: 0.3, NT: 0.5, VolT: 0.2 },
  C11d: { VT: 0.4, NT: 0.4, VolT: 0.2 },
  C12d: { VT: 0.45, NT: 0.35, VolT: 0.2 },
  C13d: { VT: 0.5, NT: 0.3, VolT: 0.2 },
};

export function TddBandsChart({ tddCode, title, description }: TddBandsChartProps) {
  const chartConfig = {
    VT: {
      label: "Vysoký tarif (VT)",
      color: "hsl(var(--chart-1))",
    },
    NT: {
      label: "Nízký tarif (NT)",
      color: "hsl(var(--chart-2))",
    },
    VolT: {
      label: "Volný tarif (VolT)",
      color: "hsl(var(--chart-3))",
    },
  };

  const bands = TDD_BANDS[tddCode as keyof typeof TDD_BANDS] || TDD_BANDS.C02d;

  const data = [
    {
      name: "Rozdělení spotřeby",
      VT: bands.VT * 100,
      NT: bands.NT * 100,
      VolT: bands.VolT * 100,
    },
  ];

  return (
    <Card className="border-primary/20" data-testid="card-tdd-bands-chart">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <span className="text-xl">{title || `TDD pásma - ${tddCode}`}</span>
        </CardTitle>
        {description && (
          <CardDescription>{description}</CardDescription>
        )}
      </CardHeader>
      <CardContent>
        <ChartContainer config={chartConfig} className="h-[200px] w-full">
          <BarChart data={data} layout="horizontal">
            <CartesianGrid strokeDasharray="3 3" className="stroke-muted" />
            <XAxis type="number" tickFormatter={(value) => `${value}%`} />
            <YAxis type="category" dataKey="name" hide />
            <ChartTooltip
              content={
                <ChartTooltipContent
                  formatter={(value) => `${Number(value).toFixed(1)}%`}
                />
              }
            />
            <Legend />
            <Bar dataKey="VT" stackId="a" fill="var(--color-VT)" />
            <Bar dataKey="NT" stackId="a" fill="var(--color-NT)" />
            <Bar dataKey="VolT" stackId="a" fill="var(--color-VolT)" />
          </BarChart>
        </ChartContainer>
      </CardContent>
    </Card>
  );
}

server/rag-service.ts.content:
/**
 * RAG (Retrieval-Augmented Generation) Service
 * 
 * Poskytuje semantic search přes znalostní bázi pomocí vector embeddings.
 * Používá OpenAI embeddings API pro deterministické vector embeddings.
 */

import { db } from "./db";
import { documents, embeddings, type Document, type Embedding } from "@shared/schema";
import { eq, sql, desc } from "drizzle-orm";
import OpenAI from "openai";

// Initialize OpenAI client
const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY,
});

/**
 * Vygeneruje embedding vector pomocí OpenAI API
 * Model: text-embedding-3-small (1536 dimensions, deterministický output)
 */
async function generateEmbedding(text: string): Promise<number[]> {
  try {
    const response = await openai.embeddings.create({
      model: "text-embedding-3-small",
      input: text.trim(),
    });

    return response.data[0].embedding;
  } catch (error) {
    console.error("[RAG] OpenAI embedding error:", error);
    throw new Error("Failed to generate embedding");
  }
}

/**
 * Rozdělí text na menší chunky pro embedding
 */
function chunkText(text: string, maxChunkSize: number = 500): string[] {
  const sentences = text.split(/[.!?]+/).filter(s => s.trim().length > 0);
  const chunks: string[] = [];
  let currentChunk = "";

  for (const sentence of sentences) {
    if ((currentChunk + sentence).length > maxChunkSize && currentChunk.length > 0) {
      chunks.push(currentChunk.trim());
      currentChunk = sentence;
    } else {
      currentChunk += (currentChunk ? ". " : "") + sentence.trim();
    }
  }

  if (currentChunk.trim().length > 0) {
    chunks.push(currentChunk.trim());
  }

  return chunks;
}

/**
 * Přidá dokument do znalostní báze a vygeneruje embeddings
 */
export async function addDocument(
  title: string,
  content: string,
  category: string,
  language: "cs" | "sk" = "cs",
  metadata?: Record<string, any>
): Promise<Document> {
  // 1. Uložit dokument
  const [document] = await db.insert(documents).values({
    title,
    content,
    category,
    language,
    metadata: metadata || null,
    isActive: 1,
  }).returning();

  // 2. Rozdělit na chunky
  const chunks = chunkText(content);

  // 3. Vygenerovat embeddings pro každý chunk
  for (let i = 0; i < chunks.length; i++) {
    const chunk = chunks[i];
    const embedding = await generateEmbedding(chunk);

    await db.insert(embeddings).values({
      documentId: document.id,
      chunkText: chunk,
      chunkIndex: i,
      embedding: JSON.parse(JSON.stringify(embedding)), // Convert to array for pgvector
    });
  }

  console.log(`[RAG] Document added: ${title} (${chunks.length} chunks)`);
  return document;
}

/**
 * Fallback znalostní báze pro případ selhání databáze
 */
const FALLBACK_KNOWLEDGE = {
  cs: [
    {
